            <p>üßë‚Äçüíª {info.bio}</p>
        </>
    );
}

    return (
        <Box ref={innerRef} display={'flex'} flexDirection={'column'} alignItems={'center'} mt={'3rem'} id={'about'}   >
            <Terminal text={aboutMeText()}/>
        </Box>
    )
}
